from heapq import *class Solution:    def getSkyline(self, LRH):        skyline = []        i, n = 0, len(LRH)        liveHR = []        while i < n or liveHR:            if not liveHR or i < n and LRH[i][0] <= liveHR[0][1]:                x = LRH[i][0]                while i < n and LRH[i][0] == x:                    heappush(liveHR, (-LRH[i][2], LRH[i][1])) # use nagetive to mimick max-heap                    i += 1            else:                x = liveHR[0][1]                while liveHR and liveHR[0][1] <= x:                    heappop(liveHR)            if liveHR:                height = -liveHR[0][0]            else:                height = 0            if not skyline or height != skyline[-1][1]:                skyline.append([x, height])        return skyline            